insert into title (code, name, name_ru, description)
values ('CН', 'CHAMPION', 'ЧЕМПИОН',
        'Самая первая ступень на пути к вершине – это титул «ЧЕМПИОН» или «CHAMPION» (93 балла по стандарту породы). Обозначение в родословных CН. Его может получить практически любая породистая кошка, начиная с возраста 10 месяцев. Условия получения достаточно просты – отсутствие явных дисквалифицирующих признаков и соответствие заявленной породе. При отсутствии конкуренции в породе, в окрасе и по половому признаку – титул получается достаточно легко. При наличии конкуренции (есть животное той же породы, того же окраса и того же пола), титул получает лучшее животное. Чтобы, как говорят «кошатники», закрыть титул ЧЕМПИОН, понадобится, в лучшем случае, 2 выставки по 2 дня каждая. Если быть точным, то 2 выставки или 3 дня участия в выставках. Если Вы не готовы тратить 2 дня выходных на получение титула для своего любимца, Вы можете принимать участие в одном дне каждой выставки. Соответственно, необходимо посетить минимум 3 выставки. Если есть конкуренция, то может понадобиться гораздо больше времени и денег для оплаты участия в выставках, чтобы получить 3 оценки CAC от трех разных судей.'),
       ('ICН', 'INTERNATIONAL CHAMPION', 'МЕЖДУНАРОДНЫЙ ЧЕМПИОН',
        'Вторая ступень – титул «МЕЖДУНАРОДНЫЙ ЧЕМПИОН» или «INTERNATIONAL CHAMPION» (95 баллов по стандарту породы). Обозначение в родословных ICН. Кот или кошка должны получить 3 оценки  CACIB от трех разных судей на международных выставках WCF в двух разных федеральных округах. Говоря простым языком, Вам нужно будет выехать со своим животным на один или два дня в другую область и получить оценку от судьи на выставке в другом городе. Хорошо, если не будет конкуренции и Ваше животное набирает свои 95 баллов и получает свою оценку CACIB. А если на выставке есть представитель вашей породы в точно таком же окрасе и того же пола? Возникает конкуренция. Титул присваивается лучшему животному. Хорошо, если Ваше животное, по мнению судьи, лучше. А если нет? Тогда Вам надо будет ехать еще раз в другой город на выставку. Никто Вам не прогарантирует, что там ситуация не повторится. А еще, данный конкретный судья может сказать, что Ваше животное не набирает 95 баллов и не дать Вам оценку CACIB. Даже при отсутствии конкуренции Вы можете уехать ни с чем. Так бывает. Также причиной неполучения титула может быть агрессивное поведение животного, плохая шоу-кондиция и тому подобное.'),
       ('GICН', 'GRAND INTERNATIONAL CHAMPION', 'БОЛЬШОЙ МЕЖДУНАРОДНЫЙ ЧЕМПИОН',
        'Третья ступень – титул «БОЛЬШОЙ МЕЖДУНАРОДНЫЙ ЧЕМПИОН» или «GRAND INTERNATIONAL CHAMPION» (97 баллов по стандарту породы). Обозначение в родословных GICН. Кошка или кот должны получить 3 оценки CAGCIB на трёх международных выставках WCF в двух разных федеральных округах от трёх разных судей. В принципе, все то же самое, что и при получении оценок CACIB. Только вырос уровень требований  к соответствию Вашей кошки или кота породе. Говоря по-простому: больше шансов уехать, не получив желанную оценку CAGCIB от судей. Не забывайте, что для Вас это может быть приключение, но для Вашего питомца – это всегда стресс!'),
       ('ECН', 'EUROPEAN CHAMPION', 'ЕВРОПЕЙСКИЙ ЧЕМПИОН',
        'Четвертая ступень – титул «ЕВРОПЕЙСКИЙ ЧЕМПИОН» или «EUROPEAN CHAMPION» (97 баллов по стандарту породы). Обозначение в родословных ECН. Кот или кошка должны получить 3 оценки CACE на трёх международных выставках WCF в трёх разных странах/континентальных регионах от трёх разных судей. Казалось бы, уровень требований остался тот же – 97 баллов. Но.. Надо ехать в другую страну или на другой континент! Выехать в соседнюю область и получить там оценку CACE можно. Но ее не зачтут при присвоении титула «ЕВРОПЕЙСКИЙ ЧЕМПИОН». Требования к территории получения титула жесткие. Должны быть 3 разные страны или континента! И вот Вы едете со своим питомцем в  другую страну… Но… я повторюсь: нет никакой гарантии, что Вы получите желанную оценку, просто приехав туда и отстояв весь день на выставке. Для нас, россиян, другими  странами могут быть любые страны, в том числе и страны бывшего СНГ (Украина, Казахстан, Белоруссия). Но ехать туда придется.'),
       ('GECН', 'GRAND EUROPEAN CHAMPION', 'БОЛЬШОЙ ЕВРОПЕЙСКИЙ ЧЕМПИОН',
        'Пятая ступень — титул «БОЛЬШОЙ ЕВРОПЕЙСКИЙ ЧЕМПИОН» или «GRAND EUROPEAN CHAMPION» (97 баллов по стандарту породы). Обозначение в родословных GECН. Кот или кошка должны получить 3 оценки GCACE на трёх международных выставках WCF в трёх разных странах/континентальных регионах от трёх разных судей. Все то же самое. Надо ездить по странам или континентам. Помнить, что конкуренцию, субъективное мнение судей и стресс для животного никто не отменял!'),
       ('WCН', 'WORLD CHAMPION', 'ЧЕМПИОН МИРА',
        'Последняя шестая ступень – титул «ЧЕМПИОН МИРА» или «WORLD CHAMPION» (98 баллов по стандарту породы). Обозначение в родословных WCН. Кот или кошка должны получить 3 оценки САСМ на трёх международных выставках в трёх разных странах от трёх разных судей, при этом одна из стран должна быть на другом континенте.');